mapper.update:
UPDATE user
SET last_login_at = NOW()
WHERE uid = #{uid}

UPDATE user
SET point_balance = #{point_balance}
WHERE uid = #{uid}

        update user set
		password = #{password},
		nickname = #{nickname},
		phone_number = #{phone_number},
		profile_img = #{profile_img}
        where uid = #{uid}

        update user set
		point_balance = point_balance + #{point}
        where uid = #{uid}

update user set
point_balance = point_balance - #{point}
where uid = #{uid}
